import{_ as n}from"./plugin-vue_export-helper-c27b6911.js";import{r,o as i,c as d,d as o,n as t,g as s,a as e,e as c,b as l}from"./app-4e3891e9.js";const m="/assets/images/resource/tools/daily-tools/system-info.png",h="/assets/images/resource/tools/development-tools/safari-ndm.png",p="/assets/images/resource/tools/development-tools/safari-dev.png",u={},g=c('<h1 id="日常工具-mac-版" tabindex="-1"><a class="header-anchor" href="#日常工具-mac-版" aria-hidden="true">#</a> 日常工具（Mac 版）</h1><p>刚从 Windows 换到 Mac 时，确实对这个安装软件晕得很，时间久了其实也觉得差不多，无非也是商店和官网两个下载途径，但是在苹果有了自家芯片之后，很多软件不支持这种 arm 架构，运行 X86 版本的话需要编译一版再运行，还是比较消耗算力的，有的时候甚至会出现发热或者疯狂占内存的情况，所以下载的时候除非不得已还是要下载苹果芯片的版本，查看自己目前软件是什么版的可以在 <em>系统信息</em> 这个自带的软件上查看。</p><figure><img src="'+m+'" alt="软件版本" tabindex="0" loading="lazy"><figcaption>版本信息</figcaption></figure><h2 id="deepl" tabindex="-1"><a class="header-anchor" href="#deepl" aria-hidden="true">#</a> DeepL</h2><p>一个号称全世界最准确的翻译软件，界面也很简洁，我最种草的还是因为他可以实现离线翻译。它也支持 Word 、 PDF 和 PPT 等整个文件的翻译，免费版也足够用了，如果你还没有用的惯的翻译软件可以尝试一下。</p>',5),_=e("h2",{id:"腾讯柠檬-runcat",tabindex:"-1"},[e("a",{class:"header-anchor",href:"#腾讯柠檬-runcat","aria-hidden":"true"},"#"),l(" 腾讯柠檬 & RunCat")],-1),f=e("p",null,"还记得当时那几款带 Touch Bar 的 MacBook Pro 在一些特殊场景确实是效率利器，结果很多人直接在上面养宠物，在你码字休息间隙可以摸摸它、喂喂食，好家伙，我直接就是一个很感兴趣啊，最近不是卸载掉了腾讯的柠檬清理，当然不是说这个不是好软件，主要是我这丐版确实撑不起太多东西，仔细想想依赖柠檬清理有系统监视、系统开机项、软件卸载、文件清理什么的还是很方便的，心里作用吧，如果你们想搞一个可以搞一个试试，当然如果财力雄厚，看大家更多的还是推荐 CleanMyMac X，我倒是没用过。之后就是 RunCat 这个了，如果你有的时候看不见自己的电脑有么有在认真工作，它压力大不大就觉得少点东西，你可以试试这个 RunCat，见名知意，就是根据你的 CPU 的速度来调节奔跑速度的一个菜单栏小猫，还是挺解压的一个东西，算是如果不想看冰冷的数字的一种替代方式吧。",-1),b={class:"vp-card-container"},x=e("h2",{id:"sublime-text",tabindex:"-1"},[e("a",{class:"header-anchor",href:"#sublime-text","aria-hidden":"true"},"#"),l(" Sublime Text")],-1),w=e("p",null,"相信大家都会觉得系统自带的文本编辑器的功能有点单调，尤其是在复制一些带有格式的文字，更是显得力不从心，虽然之前自己一直在用 VS Code 来作为文本编辑的主力，但是奈何它实在是太重了，很多时候有种杀鸡焉用牛刀的感觉，尤其是很多情况打开它重新打开上次意外关闭的项目，所以还是觉得本地应该有个轻量级的文本编辑器方便平时简单使用，当然这个方向竞品还是很的，因为平时工作还是 Windows 系统居多，所以大部分都是 NotePad++ 或者 UE 比较多，但是一些特殊的原因还是最后选择了 Sublime Text，虽然是付费软件，但是可以无限期的试用，在功能和使用逻辑上比较对味，如果你也有同样的需求，可以同样试一试。",-1),v=e("h2",{id:"neat-download-manager",tabindex:"-1"},[e("a",{class:"header-anchor",href:"#neat-download-manager","aria-hidden":"true"},"#"),l(" Neat Download Manager")],-1),y=e("p",null,"号称最快的浏览器下载器，最高支持 32 线程同时下载，解决浏览器下载大文件慢的问题。安装后可以自动检测本地浏览器，一键添加浏览器插件，十分方便。不过在 Safari 需要打开开发者选项中的配置才可以使用。",-1),k=e("p",null,[e("img",{src:h,alt:"Safari 注意事项",title:"Safari 注意事项",loading:"lazy"}),e("br"),e("img",{src:p,alt:"Safari 配置",title:"Safari 配置",loading:"lazy"})],-1);function C(P,S){const a=r("VPCard");return i(),d("div",null,[g,o(a,t(s({title:"DeepL 官网",desc:"点击跳转官网查看详细内容",logo:"/assets/images/resource/tools/daily-tools/DeepL_Logo_darkBlue_v2.svg",link:"https://www.deepl.com/translator",color:"rgba(173, 216, 590, 0.15)"})),null,16),_,f,e("div",b,[o(a,t(s({title:"腾讯柠檬官网",desc:"点击跳转官网查看详细内容",logo:"/assets/images/resource/tools/daily-tools/lemon.png",link:"https://lemon.qq.com",color:"rgba(173, 216, 590, 0.15)"})),null,16),o(a,t(s({title:"RunCat 官网",desc:"点击跳转官网查看详细内容",logo:"/assets/images/resource/tools/daily-tools/runcat.png",link:"https://kyome.io/runcat/index.html?lang=en",color:"rgba(173, 216, 590, 0.15)"})),null,16)]),x,w,o(a,t(s({title:"Sublime Text 官网",desc:"点击跳转官网查看详细内容",logo:"/assets/images/resource/tools/development-tools/sublime_text.png",link:"http://www.sublimetext.com",color:"rgba(173, 216, 590, 0.15)"})),null,16),v,y,k,o(a,t(s({title:"Neat Download Manager 官网",desc:"点击跳转官网查看详细内容",logo:"/assets/images/resource/tools/development-tools/neatdownloadmanager.png",link:"https://www.neatdownloadmanager.com/index.php/en/",color:"rgba(173, 216, 590, 0.15)"})),null,16)])}const T=n(u,[["render",C],["__file","daily-tools.html.vue"]]);export{T as default};
