import{_ as t}from"./plugin-vue_export-helper-c27b6911.js";import{r as a,o as r,c as l,d as i,n,g as s,a as e,b as c,e as d}from"./app-e76d9e8b.js";const h={},_=e("h1",{id:"liteflow",tabindex:"-1"},[e("a",{class:"header-anchor",href:"#liteflow","aria-hidden":"true"},"#"),c(" LiteFlow")],-1),f=d('<h2 id="规则引擎的定义" tabindex="-1"><a class="header-anchor" href="#规则引擎的定义" aria-hidden="true">#</a> 规则引擎的定义</h2><p>很多人容易把规则引擎和流程引擎的概念混在一起，这里我们先将规则引擎和流程引擎区分开：</p><ul><li><strong>规则引擎</strong>：通常是嵌入在应用程序组件中的，实现了将业务决策从应用程序代码中分离出来，并使用预定义的语义模块编写业务决策。接受数据输入，解释业务规则，并根据业务规则做出业务决策。</li><li><strong>流程引擎</strong>：实现了将多个业务参与者之间按照某种预定义的规则进行流转，通常需要涉及到角色与流程的交互。</li></ul><h2 id="liteflow适用于哪些场景" tabindex="-1"><a class="header-anchor" href="#liteflow适用于哪些场景" aria-hidden="true">#</a> LiteFlow适用于哪些场景</h2><p>LiteFlow适用于拥有复杂逻辑的业务，比如说价格引擎，下单流程等，这些业务往往都拥有很多步骤，这些步骤完全可以按照业务粒度拆分成一个个独立的组件，进行装配复用变更。使用LiteFlow，你会得到一个灵活度高，扩展性很强的系统。因为组件之间相互独立，也可以避免改一处而动全身的这样的风险。</p>',5);function p(u,g){const o=a("VPCard");return r(),l("div",null,[_,i(o,n(s({title:"LiteFlow 官网",desc:"点击跳转官网查看详细内容",logo:"/assets/images/resource/open-source-project/rule-engine/lite-flow.png",link:"https://liteflow.cc",color:"rgba(173, 216, 590, 0.15)"})),null,16),f])}const x=t(h,[["render",p],["__file","rule-engine.html.vue"]]);export{x as default};
